import React, { Component } from 'react';
import { CartContext } from '../context/CartContext';

const PRODUCTS = [
  { id: 1, name: 'T-shirt', price: 19.99, image: 'https://via.placeholder.com/300', description: 'Comfortable cotton t-shirt.' },
  { id: 2, name: 'Jeans', price: 39.99, image: 'https://via.placeholder.com/300', description: 'Stylish blue jeans.' },
  { id: 3, name: 'Sneakers', price: 59.99, image: 'https://via.placeholder.com/300', description: 'Sporty sneakers for everyday use.' },
];

class ProductDetailPage extends Component {
  static contextType = CartContext;
  render() {
    const id = parseInt(this.props.match.params.id, 10);
    const product = PRODUCTS.find(p => p.id === id);
    if (!product) return <div>Product not found.</div>;
    return (
      <div style={{ padding: 16 }}>
        <h2>{product.name}</h2>
        <img src={product.image} alt={product.name} style={{ width: 300 }} />
        <p>{product.description}</p>
        <p>Price: ${product.price.toFixed(2)}</p>
        <button onClick={() => this.context.addToCart(product)}>Add to Cart</button>
      </div>
    );
  }
}

export default ProductDetailPage;
