import React, { Component } from 'react';
import { Link } from 'react-router-dom';

/**
 * HomePage (Class Component)
 * - Uniform product grid (no up/down jumpy images)
 * - Header Cart button with live count
 * - Slide-in Cart Drawer with remove & quantity controls
 * - Fetches products from fakestoreapi
 */

export default class HomePage extends Component {
  state = {
    products: [],
    loading: true,
    error: '',
    cart: [], // {id, title, price, image, qty}
    isCartOpen: false,
  };

  componentDidMount() {
    this.fetchProducts();
  }

  async fetchProducts() {
    this.setState({ loading: true, error: '' });
    try {
      const res = await fetch('https://fakestoreapi.com/products');
      const data = await res.json();
      this.setState({ products: data, loading: false });
    } catch (e) {
      this.setState({ error: 'Failed to load products.', loading: false });
    }
  }

  // --- Cart helpers ---
  addToCart = (product) => {
    this.setState((prev) => {
      const exists = prev.cart.find((i) => i.id === product.id);
      if (exists) {
        return {
          cart: prev.cart.map((i) =>
            i.id === product.id ? { ...i, qty: i.qty + 1 } : i
          ),
        };
      }
      return {
        cart: [
          ...prev.cart,
          { id: product.id, title: product.title, price: product.price, image: product.image, qty: 1 },
        ],
      };
    });
  };

  decrementQty = (id) => {
    this.setState((prev) => {
      const item = prev.cart.find((i) => i.id === id);
      if (!item) return null;
      if (item.qty === 1) {
        return { cart: prev.cart.filter((i) => i.id !== id) };
      }
      return { cart: prev.cart.map((i) => (i.id === id ? { ...i, qty: i.qty - 1 } : i)) };
    });
  };

  removeFromCart = (id) => {
    this.setState((prev) => ({ cart: prev.cart.filter((i) => i.id !== id) }));
  };

  clearCart = () => this.setState({ cart: [] });

  getCartCount = () => this.state.cart.reduce((sum, i) => sum + i.qty, 0);
  getCartTotal = () => this.state.cart.reduce((sum, i) => sum + i.qty * i.price, 0);

  toggleCart = () => this.setState((p) => ({ isCartOpen: !p.isCartOpen }));

  render() {
    const { products, loading, error, isCartOpen, cart } = this.state;

    return (
      <div style={styles.page}>
        {/* Header with cart button */}
        <header style={styles.header}>
          <div style={styles.brandWrap}>
            <svg width="36" height="36" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M7 18a2 2 0 1 0 0 4 2 2 0 0 0 0-4Zm10 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4ZM3.17 16l1.54-7h13.58l1.54 7H3.17ZM6.16 7l1.25-5h9.18l1.25 5H6.16Z" fill="#fff"/>
            </svg>
            <h1 style={styles.brand}>Gogo Store</h1>
          </div>

          <button aria-label={`Open cart with ${this.getCartCount()} items`} onClick={this.toggleCart} style={styles.cartBtn}>
            <span role="img" aria-hidden>ðŸ›’</span>
            <span style={styles.cartText}>Cart</span>
            <span style={styles.cartBadge}>{this.getCartCount()}</span>
          </button>
        </header>

        {/* Hero */}
        <section style={styles.hero}>
          <h2 style={{margin: 0}}>Shop the best products online</h2>
          <p style={{opacity: .9, marginTop: 6}}>Free shipping on orders over $50</p>
          <a href="#products" style={styles.cta}>Shop Now</a>
        </section>

        {/* Product Gallery (uniform thumbnails) */}
        <div style={styles.thumbStrip}>
          {products.slice(0, 12).map((p) => (
            <div key={p.id} style={styles.thumbBox}>
              <img src={p.image} alt={p.title} style={styles.thumbImg} />
            </div>
          ))}
        </div>

        {/* Product Grid */}
        <h3 id="products" style={styles.sectionTitle}>Product Listing</h3>

        {loading && <div style={styles.center}>Loadingâ€¦</div>}
        {error && <div style={{...styles.center, color: 'crimson'}}>{error}</div>}

        {!loading && !error && (
          <div style={styles.grid} className="product-grid">
            {products.map((product) => (
              <div key={product.id} style={styles.card} className="product-card">
                <div style={styles.imageWrap}>
                  <img src={product.image} alt={product.title} style={styles.cardImg} />
                </div>
                <h4 style={styles.title} title={product.title}>{product.title}</h4>
                <p style={styles.price}>${product.price.toFixed(2)}</p>
                <div style={styles.actions}>
                  <Link to={`/product/${product.id}/details`} style={styles.link}>View Details</Link>
                  <button onClick={() => this.addToCart(product)} style={styles.primaryBtn}>
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Cart Drawer */}
        <aside aria-hidden={!isCartOpen} style={{
          ...styles.drawer,
          transform: isCartOpen ? 'translateX(0)' : 'translateX(110%)',
        }}>
          <div style={styles.drawerHeader}>
            <h3 style={{margin: 0}}>Your Cart ({this.getCartCount()})</h3>
            <button onClick={this.toggleCart} style={styles.iconBtn} aria-label="Close cart">âœ•</button>
          </div>

          <div style={styles.drawerBody}>
            {cart.length === 0 ? (
              <p style={{opacity: .8}}>Your cart is empty.</p>
            ) : (
              cart.map((item) => (
                <div key={item.id} style={styles.cartRow}>
                  <div style={styles.cartThumbWrap}>
                    <img src={item.image} alt={item.title} style={styles.cartThumb} />
                  </div>
                  <div style={{flex: 1}}>
                    <div style={styles.cartTitle} title={item.title}>{item.title}</div>
                    <div style={{fontWeight: 600}}>${(item.price * item.qty).toFixed(2)}</div>
                    <div style={styles.qtyRow}>
                      <button onClick={() => this.decrementQty(item.id)} style={styles.qtyBtn} aria-label="Decrease quantity">âˆ’</button>
                      <span style={{minWidth: 24, textAlign: 'center'}}>{item.qty}</span>
                      <button onClick={() => this.addToCart(item)} style={styles.qtyBtn} aria-label="Increase quantity">+</button>
                    </div>
                  </div>
                  <button onClick={() => this.removeFromCart(item.id)} style={styles.removeBtn} aria-label={`Remove ${item.title}`}>
                    Remove
                  </button>
                </div>
              ))
            )}
          </div>

          <div style={styles.drawerFooter}>
            <div style={styles.summaryRow}>
              <span>Subtotal</span>
              <strong>${this.getCartTotal().toFixed(2)}</strong>
            </div>
            <div style={{display: 'flex', gap: 8}}>
              <button onClick={this.clearCart} style={styles.secondaryBtn}>Clear</button>
              <button style={styles.checkoutBtn}>Checkout</button>
            </div>
          </div>
        </aside>

        {/* Backdrop for drawer */}
        {isCartOpen && <div onClick={this.toggleCart} style={styles.backdrop} />}

        {/* Responsive tweaks */}
        <style>{`
          @media (max-width: 640px) {
            .product-grid { grid-template-columns: repeat(auto-fill, minmax(180px, 1fr)) !important; }
          }
        `}</style>
      </div>
    );
  }
}

// --- Styles ---
const styles = {
  page: { padding: 16, fontFamily: 'Segoe UI, Arial, sans-serif', background: 'linear-gradient(180deg,#f7fbff, #ffffff)' },
  header: {
    display: 'flex', alignItems: 'center', justifyContent: 'space-between',
    padding: '14px 18px', marginBottom: 16,
    background: 'linear-gradient(90deg,#007bff 60%, #00c6ff 100%)', color: '#fff',
    borderRadius: 14, boxShadow: '0 8px 24px rgba(0,0,0,.12)'
  },
  brandWrap: { display: 'flex', alignItems: 'center', gap: 10 },
  brand: { margin: 0, fontSize: 24, fontWeight: 800, letterSpacing: .5 },
  cartBtn: {
    display: 'inline-flex', alignItems: 'center', gap: 10,
    background: '#ffffff', color: '#007bff', border: 'none', borderRadius: 9999,
    padding: '8px 14px', cursor: 'pointer', fontWeight: 700, boxShadow: '0 4px 14px rgba(0,0,0,.15)'
  },
  cartText: { fontSize: 16 },
  cartBadge: { background: '#007bff', color: '#fff', borderRadius: 9999, padding: '2px 8px', fontSize: 12, minWidth: 20, textAlign: 'center' },

  hero: {
    textAlign: 'center', padding: '44px 16px', background: 'linear-gradient(120deg,#e6f1ff 40%, #f4f9ff 100%)',
    margin: '12px 0 22px', borderRadius: 16, boxShadow: '0 6px 18px rgba(0,0,0,.06)'
  },
  cta: { display: 'inline-block', marginTop: 14, padding: '10px 18px', background: '#007bff', color: '#fff', borderRadius: 9999, textDecoration: 'none', fontWeight: 700 },

  thumbStrip: {
    maxWidth: 1200, margin: '0 auto 24px', display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(90px, 1fr))', gap: 12,
    background: 'linear-gradient(90deg,#eef6ff,#f9fbff)', padding: 12,
    borderRadius: 14, boxShadow: '0 2px 10px rgba(0,0,0,.05)'
  },
  thumbBox: { background: '#fff', borderRadius: 10, height: 90, display: 'grid', placeItems: 'center', boxShadow: '0 1px 6px rgba(0,0,0,.05)' },
  thumbImg: { maxHeight: '80%', maxWidth: '80%', objectFit: 'contain' },

  sectionTitle: { textAlign: 'center', fontSize: 28, fontWeight: 800, margin: '12px 0 20px' },

  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))',
    gap: 20,
    maxWidth: 1200,
    margin: '0 auto',
  },
  card: {
    background: 'linear-gradient(135deg,#f8faff 75%, #e3f0ff 100%)',
    borderRadius: 16,
    boxShadow: '0 10px 26px rgba(0,123,255,.12)',
    padding: 14,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch',
    transition: 'transform .2s, box-shadow .2s',
  },
  imageWrap: {
    height: 180, // FIX: uniform height so images don't cause up/down jumps
    display: 'grid',
    placeItems: 'center',
    background: '#fff',
    borderRadius: 12,
    boxShadow: 'inset 0 0 0 1px rgba(0,0,0,.04)'
  },
  cardImg: { maxHeight: '90%', maxWidth: '90%', objectFit: 'contain' },
  title: {
    fontSize: 16, fontWeight: 700, color: '#0f172a', margin: '12px 4px 4px',
    display: '-webkit-box', WebkitLineClamp: 2, WebkitBoxOrient: 'vertical', overflow: 'hidden'
  },
  price: { fontSize: 18, fontWeight: 800, color: '#111827', margin: '2px 4px 10px' },
  actions: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: 8 },
  link: { color: '#0ea5e9', textDecoration: 'underline', fontWeight: 600, fontSize: 14 },
  primaryBtn: {
    background: 'linear-gradient(90deg,#007bff,#00c6ff)', color: '#fff', border: 'none',
    borderRadius: 10, padding: '8px 12px', cursor: 'pointer', fontWeight: 800
  },

  center: { textAlign: 'center', padding: 24 },

  // Drawer
  drawer: {
    position: 'fixed', top: 0, right: 0, height: '100%', width: 360,
    background: '#fff', boxShadow: '0 0 0 1px rgba(0,0,0,.06), 0 20px 80px rgba(0,0,0,.2)',
    transition: 'transform .25s ease', zIndex: 50, display: 'flex', flexDirection: 'column'
  },
  drawerHeader: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: 16, borderBottom: '1px solid #eef2f7' },
  iconBtn: { background: 'transparent', border: 'none', fontSize: 20, cursor: 'pointer' },
  drawerBody: { padding: 16, overflowY: 'auto', flex: 1 },
  cartRow: { display: 'flex', gap: 12, alignItems: 'center', padding: '10px 0', borderBottom: '1px dashed #e5e7eb' },
  cartThumbWrap: { width: 64, height: 64, borderRadius: 10, background: '#f9fafb', display: 'grid', placeItems: 'center', overflow: 'hidden' },
  cartThumb: { maxWidth: '90%', maxHeight: '90%', objectFit: 'contain' },
  cartTitle: { fontWeight: 700, marginBottom: 6, display: '-webkit-box', WebkitLineClamp: 2, WebkitBoxOrient: 'vertical', overflow: 'hidden' },
  qtyRow: { display: 'inline-flex', alignItems: 'center', gap: 10, marginTop: 6 },
  qtyBtn: { width: 28, height: 28, borderRadius: 8, border: '1px solid #d1d5db', background: '#fff', cursor: 'pointer' },
  removeBtn: { marginLeft: 8, background: 'transparent', border: 'none', color: '#ef4444', cursor: 'pointer', fontWeight: 700 },
  drawerFooter: { padding: 16, borderTop: '1px solid #eef2f7', display: 'grid', gap: 10 },
  summaryRow: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', fontSize: 16 },
  secondaryBtn: { background: '#f3f4f6', border: '1px solid #e5e7eb', borderRadius: 10, padding: '10px 12px', cursor: 'pointer', fontWeight: 700 },
  checkoutBtn: { background: '#111827', color: '#fff', border: 'none', borderRadius: 10, padding: '10px 12px', cursor: 'pointer', fontWeight: 800 },

  backdrop: { position: 'fixed', inset: 0, background: 'rgba(0,0,0,.35)', zIndex: 40 }
};
