import React, { useState, useEffect } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [page, setPage] = useState("home"); // 🔹 Navigation state
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("all");

  // ✅ API se products fetch
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setProducts(data));

    fetch("https://fakestoreapi.com/products/categories")
      .then((res) => res.json())
      .then((data) => setCategories(data));
  }, []);

  // ✅ Add to Cart
  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  // ✅ Remove from Cart
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // ✅ Search + Category filter
  const filteredProducts = products.filter(
    (p) =>
      p.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === "all" || p.category === selectedCategory)
  );

  return (
    <div style={styles.app}>
      {/* 🔹 Navbar */}
      <nav style={styles.navbar}>
        <h2 style={{ cursor: "pointer" }} onClick={() => setPage("home")}>
          🛍 MyStore
        </h2>
        <div style={styles.navLinks}>
          <button style={styles.navBtn} onClick={() => setPage("home")}>
            Home
          </button>
          <button style={styles.navBtn} onClick={() => setPage("about")}>
            About
          </button>
          <button style={styles.navBtn} onClick={() => setPage("details")}>
            Details
          </button>
        </div>
        <div style={styles.searchBox}>
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={styles.searchInput}
          />
          <button style={styles.searchBtn}>🔍</button>
          <button style={styles.cartBtn} onClick={() => setShowCart(!showCart)}>
            Cart ({cart.length})
          </button>
        </div>
      </nav>

      {/* 🔹 Pages */}
      {page === "home" && (
        <>
          {/* Category Section */}
          <div style={styles.categoryBar}>
            <button
              style={styles.catBtn}
              onClick={() => setSelectedCategory("all")}
            >
              All
            </button>
            {categories.map((cat) => (
              <button
                key={cat}
                style={styles.catBtn}
                onClick={() => setSelectedCategory(cat)}
              >
                {cat}
              </button>
            ))}
          </div>

          {/* Product Grid */}
          <div style={styles.grid}>
            {filteredProducts.map((product) => (
              <div key={product.id} style={styles.card}>
                <div style={styles.imageWrap}>
                  <img
                    src={product.image}
                    alt={product.title}
                    style={styles.cardImg}
                  />
                </div>
                <h3>{product.title.slice(0, 30)}...</h3>
                <p>₹{product.price}</p>
                <button
                  style={styles.addBtn}
                  onClick={() => addToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        </>
      )}

      {page === "about" && (
        <div style={styles.page}>
          <h1>About Us</h1>
          <p>
            Welcome to MyStore! 🛍 This is a demo e-commerce site built with
            React.
          </p>
        </div>
      )}

      {page === "details" && (
        <div style={styles.page}>
          <h1>Details</h1>
          <p>Here you can add product details or company info.</p>
        </div>
      )}

      {/* 🔹 Cart Drawer */}
      {showCart && (
        <div style={styles.cartDrawer}>
          <h2>Your Cart</h2>
          {cart.length === 0 ? (
            <p>No items added</p>
          ) : (
            cart.map((item) => (
              <div key={item.id} style={styles.cartItem}>
                <span>{item.title.slice(0, 20)}...</span>
                <button onClick={() => removeFromCart(item.id)}>❌</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

const styles = {
  app: { fontFamily: "sans-serif", padding: 20 },

  // Navbar
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "12px 20px",
    background: "#2563eb",
    color: "#fff",
    borderRadius: 12,
  },
  navLinks: { display: "flex", gap: 12 },
  navBtn: {
    background: "#1e40af",
    color: "#fff",
    border: "none",
    padding: "6px 12px",
    borderRadius: 6,
    cursor: "pointer",
  },
  searchBox: { display: "flex", gap: 8, alignItems: "center" },
  searchInput: { padding: 6, borderRadius: 6, border: "1px solid #ccc" },
  searchBtn: {
    padding: "6px 10px",
    background: "#1e3a8a",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },
  cartBtn: {
    padding: "6px 12px",
    background: "#facc15",
    color: "#000",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // Category Bar
  categoryBar: { display: "flex", gap: 10, margin: "20px 0" },
  catBtn: {
    padding: "6px 12px",
    background: "#e5e7eb",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // Product Grid
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,minmax(220px,1fr))",
    gap: 16,
    marginTop: 20,
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: 12,
    padding: 16,
    textAlign: "center",
    background: "#fff",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
  },
  imageWrap: {
    height: 200,
    width: "100%",
    display: "grid",
    placeItems: "center",
    background: "#f9fafb",
    borderRadius: 12,
  },
  cardImg: { width: 120, height: 120, objectFit: "contain" },
  addBtn: {
    marginTop: 10,
    padding: "6px 12px",
    background: "#16a34a",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // Cart Drawer
  cartDrawer: {
    position: "fixed",
    right: 0,
    top: 0,
    height: "100%",
    width: 300,
    background: "#fff",
    boxShadow: "-2px 0 6px rgba(0,0,0,0.1)",
    padding: 20,
    overflowY: "auto",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "8px 0",
    borderBottom: "1px solid #eee",
  },

  // Pages
  page: { padding: 20, textAlign: "center" },
};

export default App;
