import React, { Component } from 'react';
import { CartContext } from '../context/CartContext';
import { Link } from 'react-router-dom';

class ProductDetailPage extends Component {
  static contextType = CartContext;

  state = {
    product: null,
    loading: true,
    error: null,
  };

  componentDidMount() {
    const id = parseInt(this.props.match?.params?.id || this.props.params?.id, 10);

    fetch(`https://fakestoreapi.com/products/${id}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch product");
        return res.json();
      })
      .then(data => this.setState({ product: data, loading: false }))
      .catch(err => this.setState({ error: err.message, loading: false }));
  }

  render() {
    const { product, loading, error } = this.state;

    if (loading) return <div style={{ padding: 16 }}>Loading...</div>;
    if (error) return <div style={{ padding: 16, color: 'red' }}>{error}</div>;
    if (!product) return <div style={{ padding: 16 }}>Product not found.</div>;

    return (
      <div style={{ padding: 16 }}>
        <Link
          to="/"
          style={{
            color: '#007bff',
            textDecoration: 'underline',
            marginBottom: 16,
            display: 'inline-block'
          }}
        >
          Back to Home
        </Link>

        <h2>{product.title}</h2>
        <img
          src={product.image}
          alt={product.title}
          style={{ width: 300, maxWidth: '100%', borderRadius: 8 }}
        />
        <p>{product.description}</p>
        <p style={{ fontWeight: 'bold' }}>Price: ${product.price.toFixed(2)}</p>

        <button
          onClick={() => this.context.addToCart(product)}
          style={{
            padding: '8px 16px',
            background: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: 4,
            cursor: 'pointer',
            fontWeight: 'bold',
            marginTop: 12
          }}
        >
          Add to MyCart
        </button>
      </div>
    );
  }
}

export default ProductDetailPage;
