import React, { useState, useEffect } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("all");

  // ‚úÖ Products fetch
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setProducts(data));

    fetch("https://fakestoreapi.com/products/categories")
      .then((res) => res.json())
      .then((data) => setCategories(data));
  }, []);

  // ‚úÖ Add to Cart
  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  // ‚úÖ Remove from Cart
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // ‚úÖ Filtered Products (search + category)
  const filteredProducts = products.filter((p) => {
    const matchesSearch = p.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || p.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  return (
    <div style={styles.app}>
      {/* üîπ NavBar */}
      <nav style={styles.navbar}>
        <div style={styles.navLinks}>
          <a href="#home">Home</a>
          <a href="#about">About</a>
          <a href="#details">Details</a>
        </div>

        <div style={styles.navRight}>
          <input
            type="text"
            placeholder="Search..."
            style={styles.search}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button style={styles.cartBtn} onClick={() => setShowCart(!showCart)}>
            Cart ({cart.length})
          </button>
        </div>
      </nav>

      {/* üîπ Category Section */}
      <div style={styles.category}>
        <button
          style={selectedCategory === "all" ? styles.activeCatBtn : styles.catBtn}
          onClick={() => setSelectedCategory("all")}
        >
          All
        </button>
        {categories.map((cat) => (
          <button
            key={cat}
            style={selectedCategory === cat ? styles.activeCatBtn : styles.catBtn}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* üîπ Product Grid */}
      <div style={styles.grid}>
        {filteredProducts.map((product) => (
          <div key={product.id} style={styles.card}>
            <div style={styles.imageWrap}>
              <img src={product.image} alt={product.title} style={styles.cardImg} />
            </div>
            <h3>{product.title.slice(0, 30)}...</h3>
            <p>‚Çπ{product.price}</p>
            <button style={styles.addBtn} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* üîπ Cart Drawer */}
      {showCart && (
        <div style={styles.cartDrawer}>
          <h2>Your Cart</h2>
          {cart.length === 0 ? (
            <p>No items added</p>
          ) : (
            cart.map((item) => (
              <div key={item.id} style={styles.cartItem}>
                <span>{item.title.slice(0, 20)}...</span>
                <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

const styles = {
  app: { fontFamily: "sans-serif" },

  // ‚úÖ Navbar
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "12px 20px",
    background: "#2563eb",
    color: "#fff",
  },
  navLinks: {
    display: "flex",
    gap: 20,
  },
  navRight: {
    display: "flex",
    gap: 10,
    alignItems: "center",
  },
  search: {
    padding: "6px 10px",
    borderRadius: 6,
    border: "none",
  },
  cartBtn: {
    padding: "8px 14px",
    background: "#16a34a",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // ‚úÖ Category
  category: {
    display: "flex",
    gap: 10,
    margin: "20px 0",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  catBtn: {
    padding: "8px 16px",
    background: "#e5e7eb",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },
  activeCatBtn: {
    padding: "8px 16px",
    background: "#2563eb",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // ‚úÖ Products
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,minmax(220px,1fr))",
    gap: 16,
    padding: 20,
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: 12,
    padding: 16,
    textAlign: "center",
    background: "#fff",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
  },
  imageWrap: {
    height: 200,
    width: "100%",
    display: "grid",
    placeItems: "center",
    background: "#f9fafb",
    borderRadius: 12,
  },
  cardImg: {
    width: 120,
    height: 120,
    objectFit: "contain",
  },
  addBtn: {
    marginTop: 10,
    padding: "6px 12px",
    background: "#16a34a",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },

  // ‚úÖ Cart Drawer
  cartDrawer: {
    position: "fixed",
    right: 0,
    top: 0,
    height: "100%",
    width: 300,
    background: "#fff",
    boxShadow: "-2px 0 6px rgba(0,0,0,0.1)",
    padding: 20,
    overflowY: "auto",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "8px 0",
    borderBottom: "1px solid #eee",
  },
};

export default App;
