import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const styles = {
  container: {
    padding: 16,
  },
  grid: {
    display: 'flex',
    gap: 24,
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  card: {
    border: '1px solid #ccc',
    padding: 16,
    width: 220,
    boxSizing: 'border-box',
    borderRadius: 8,
    background: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    marginBottom: 24,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  img: {
    width: '100%',
    maxWidth: 180,
    borderRadius: 4,
    marginBottom: 8,
  },
  button: {
    marginTop: 8,
    padding: '8px 16px',
    background: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: 4,
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background 0.2s',
  },
};

class HomePage extends Component {
  static contextType = CartContext;
  state = {
    products: [],
    loading: true,
    error: '',
  };

  componentDidMount() {
    this.fetchProducts();
  }

  fetchProducts = async () => {
    this.setState({ loading: true });
    try {
      const response = await fetch('https://fakestoreapi.com/products');
      const data = await response.json();
      this.setState({ products: data, loading: false });
    } catch (err) {
      this.setState({ error: 'Failed to load products.', loading: false });
    }
  };

  handleAddToCart = (product) => {
    this.context.addToCart(product);
  };

  render() {
    const { products, loading, error } = this.state;
    return (
      <div style={styles.container}>
        <h2 style={{ textAlign: 'center' }}>Product Listing</h2>
        {loading ? (
          <div style={{ textAlign: 'center', margin: 32 }}>Loading...</div>
        ) : error ? (
          <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>
        ) : (
          <div style={styles.grid} className='product-grid'>
            {products.map(product => (
              <div key={product.id} style={styles.card} className='product-card'>
                <img src={product.image} alt={product.title} style={styles.img} />
                <h3>{product.title}</h3>
                <p style={{ fontWeight: 'bold', color: '#333' }}>${product.price.toFixed(2)}</p>
                <Link to={`/product/${product.id}/details`} style={{ color: '#007bff', textDecoration: 'underline', marginBottom: 8 }}>View Details</Link>
                <button onClick={() => this.handleAddToCart(product)} style={styles.button}>
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        )}
        <style>{`
          @media (max-width: 600px) {
            .product-grid {
              flex-direction: column !important;
              gap: 12px !important;
            }
            .product-card {
              width: 100% !important;
              max-width: 320px !important;
              margin: 0 auto 16px auto !important;
            }
          }
        `}</style>
      </div>
    );
  }
}

export default HomePage;
