import React, { Component } from 'react';
import { CartContext } from '../context/CartContext';
import { Link } from 'react-router-dom';

const PRODUCTS = [
  { id: 1, title: 'T-shirt', price: 19.99, image: 'https://via.placeholder.com/300', description: 'Comfortable cotton t-shirt.' },
  { id: 2, title: 'Jeans', price: 39.99, image: 'https://via.placeholder.com/300', description: 'Stylish blue jeans.' },
  { id: 3, title: 'Sneakers', price: 59.99, image: 'https://via.placeholder.com/300', description: 'Sporty sneakers for everyday use.' },
];

class ProductDetailPage extends Component {
  static contextType = CartContext;
  render() {
    const id = parseInt(this.props.match?.params?.id || this.props.params?.id, 10);
    const product = PRODUCTS.find(p => p.id === id);
    if (!product) return <div style={{ padding: 16 }}>Product not found.</div>;
    return (
      <div style={{ padding: 16 }}>
        <Link to='/' style={{ color: '#007bff', textDecoration: 'underline', marginBottom: 16, display: 'inline-block' }}>Back to Home</Link>
        <h2>{product.title}</h2>
        <img src={product.image} alt={product.title} style={{ width: 300, maxWidth: '100%', borderRadius: 8 }} />
        <p>{product.description}</p>
        <p style={{ fontWeight: 'bold' }}>Price: ${product.price.toFixed(2)}</p>
        <button onClick={() => this.context.addToCart(product)} style={{ padding: '8px 16px', background: '#007bff', color: '#fff', border: 'none', borderRadius: 4, cursor: 'pointer', fontWeight: 'bold', marginTop: 12 }}>Add to MyCart</button>
      </div>
    );
  }
}

export default ProductDetailPage;
