import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const styles = {
  container: {
    padding: 16,
  },
  grid: {
    display: 'flex',
    gap: 24,
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  card: {
    border: '1px solid #ccc',
    padding: 16,
    width: 220,
    boxSizing: 'border-box',
    borderRadius: 8,
    background: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    marginBottom: 24,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  img: {
    width: '100%',
    maxWidth: 180,
    borderRadius: 4,
    marginBottom: 8,
  },
  button: {
    marginTop: 8,
    padding: '8px 16px',
    background: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: 4,
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background 0.2s',
  },
};

class HomePage extends Component {
  static contextType = CartContext;
  state = {
    products: [],
    loading: true,
    error: '',
  };

  componentDidMount() {
    this.fetchProducts();
  }

  fetchProducts = async () => {
    this.setState({ loading: true });
    try {
      const response = await fetch('https://fakestoreapi.com/products');
      const data = await response.json();
      this.setState({ products: data, loading: false });
    } catch (err) {
      this.setState({ error: 'Failed to load products.', loading: false });
    }
  };

  handleAddToCart = (product) => {
    this.context.addToCart(product);
  };

  render() {
    const { products, loading, error } = this.state;
    return (
      <div style={styles.container}>
        <header style={{ textAlign: 'center', padding: '32px 0', background: 'linear-gradient(90deg, #007bff 60%, #00c6ff 100%)', color: '#fff', marginBottom: 40, borderRadius: 12, display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 20, boxShadow: '0 4px 16px rgba(0,0,0,0.10)' }}>
          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ verticalAlign: 'middle', marginRight: 12 }}>
            <path d="M7 18c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm10 0c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm-12.83-2l1.54-7h13.58l1.54 7H4.17zM6.16 7l1.25-5h9.18l1.25 5H6.16z" fill="#fff"/>
          </svg>
          <div>
            <h1 style={{ margin: 0, fontSize: 36, letterSpacing: 2, fontWeight: 700, textShadow: '0 2px 8px rgba(0,0,0,0.10)' }}>Gogo Store</h1>
            <p style={{ margin: 0, fontSize: 20, fontWeight: '300', letterSpacing: 1 }}>Shop the best products online</p>
          </div>
        </header>
        <h2 style={{ textAlign: 'center', marginBottom: 32, color: '#222', fontSize: 28, fontWeight: 600 }}>Product Listing</h2>
        {loading ? (
          <div style={{ textAlign: 'center', margin: 32 }}>Loading...</div>
        ) : error ? (
          <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>
        ) : (
          <div style={{ ...styles.grid, maxWidth: 1200, margin: '0 auto' }} className='product-grid'>
            {products.map(product => (
              <div key={product.id} style={{ ...styles.card, boxShadow: '0 8px 24px rgba(0,0,0,0.10)', transition: 'transform 0.2s, box-shadow 0.2s', background: '#fff', border: 'none', cursor: 'pointer', position: 'relative', overflow: 'hidden', borderRadius: 16 }} className='product-card'
                onMouseEnter={e => {e.currentTarget.style.transform = 'scale(1.04)'; e.currentTarget.style.boxShadow = '0 12px 32px rgba(0,123,255,0.15)';}}
                onMouseLeave={e => {e.currentTarget.style.transform = 'scale(1)'; e.currentTarget.style.boxShadow = '0 8px 24px rgba(0,0,0,0.10)';}}>
                <img src={product.image} alt={product.title} style={{ ...styles.img, marginBottom: 18, background: '#f6f6f6', padding: 12, borderRadius: 12, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', transition: 'box-shadow 0.2s' }} />
                <h3 style={{ margin: '10px 0', fontSize: 22, color: '#007bff', fontWeight: 600 }}>{product.title}</h3>
                <p style={{ fontWeight: 'bold', color: '#222', fontSize: 19, margin: '6px 0' }}>${product.price.toFixed(2)}</p>
                <Link to={`/product/${product.id}/details`} style={{ color: '#00c6ff', textDecoration: 'underline', marginBottom: 10, fontSize: 16, fontWeight: 500, outline: 'none' }} tabIndex={0} aria-label={`View details for ${product.title}`}>View Details</Link>
                <button onClick={() => this.handleAddToCart(product)} style={{ ...styles.button, width: '100%', fontSize: 17, marginTop: 16, background: 'linear-gradient(90deg, #007bff 60%, #00c6ff 100%)', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', outline: 'none', border: 'none', fontWeight: 600, letterSpacing: 1, transition: 'background 0.2s, box-shadow 0.2s' }} aria-label={`Add ${product.title} to cart`}>
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        )}
        <style>{`
          @media (max-width: 600px) {
            .product-grid {
              flex-direction: column !important;
              gap: 16px !important;
              max-width: 100% !important;
            }
            .product-card {
              width: 100% !important;
              max-width: 340px !important;
              margin: 0 auto 18px auto !important;
              box-shadow: 0 4px 16px rgba(0,0,0,0.10) !important;
              border-radius: 16px !important;
            }
          }
        `}</style>
      </div>
    );
  }
}

export default HomePage;
