import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const PRODUCTS = [
  { id: 1, name: 'T-shirt', price: 19.99, image: 'https://via.placeholder.com/150' },
  { id: 2, name: 'Jeans', price: 39.99, image: 'https://via.placeholder.com/150' },
  { id: 3, name: 'Sneakers', price: 59.99, image: 'https://via.placeholder.com/150' },
];

class HomePage extends Component {
  static contextType = CartContext;
  handleAddToCart = (product) => {
    this.context.addToCart(product);
  };
  render() {
    return (
      <div style={{ padding: 16 }}>
        <h2>Product Listing</h2>
        <div style={{ display: 'flex', gap: 24 }}>
          {PRODUCTS.map(product => (
            <div key={product.id} style={{ border: '1px solid #ccc', padding: 16, width: 200 }}>
              <img src={product.image} alt={product.name} style={{ width: '100%' }} />
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              <Link to={`/product/${product.id}/details`}>View Details</Link>
              <br />
              <button onClick={() => this.handleAddToCart(product)} style={{ marginTop: 8 }}>Add to Cart</button>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default HomePage;
