import React, { useState, useEffect } from 'react';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  return (
    <div style={styles.app}>
      <header style={styles.header}>
        <h1>üõí My Store</h1>
        <button style={styles.cartBtn} onClick={() => setShowCart(!showCart)}>
          Cart ({cart.length})
        </button>
      </header>

      <div style={styles.grid}>
        {products.map(product => (
          <div key={product.id} style={styles.card}>
            <div style={styles.imageWrap}>
              <img src={product.image} alt={product.title} style={styles.cardImg} />
            </div>
            <h3>{product.title.slice(0, 30)}...</h3>
            <p>‚Çπ{product.price}</p>
            <button style={styles.addBtn} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {showCart && (
        <div style={styles.cartDrawer}>
          <h2>Your Cart</h2>
          {cart.length === 0 ? <p>No items added</p> : cart.map(item => (
            <div key={item.id} style={styles.cartItem}>
              <span>{item.title.slice(0, 20)}...</span>
              <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  app: { fontFamily: 'sans-serif', padding: 20 },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  cartBtn: { padding: '8px 16px', background: '#2563eb', color: '#fff', border: 'none', borderRadius: 8 },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(220px,1fr))', gap: 16, marginTop: 20 },
  card: { border: '1px solid #ddd', borderRadius: 12, padding: 16, textAlign: 'center', background: '#fff', boxShadow: '0 2px 6px rgba(0,0,0,0.1)' },

  // üîΩ bas ye 2 change kiye hain
  imageWrap: { 
    height: 200, 
    width: '100%', 
    display: 'grid', 
    placeItems: 'center', 
    background: '#f9fafb', 
    borderRadius: 12 
  },
  cardImg: { 
    width: 120, 
    height: 120, 
    objectFit: 'contain' 
  },

  addBtn: { marginTop: 10, padding: '6px 12px', background: '#16a34a', color: '#fff', border: 'none', borderRadius: 6 },
  cartDrawer: { position: 'fixed', right: 0, top: 0, height: '100%', width: 300, background: '#fff', boxShadow: '-2px 0 6px rgba(0,0,0,0.1)', padding: 20, overflowY: 'auto' },
  cartItem: { display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #eee' }
};

export default App;
