import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const styles = {
  container: {
    padding: 16,
  },
  grid: {
    display: 'flex',
    gap: 24,
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  card: {
    border: '1px solid #ccc',
    padding: 16,
    width: 220,
    boxSizing: 'border-box',
    borderRadius: 8,
    background: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    marginBottom: 24,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  img: {
    width: '100%',
    maxWidth: 180,
    borderRadius: 4,
    marginBottom: 8,
  },
  button: {
    marginTop: 8,
    padding: '8px 16px',
    background: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: 4,
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background 0.2s',
  },
};

class HomePage extends Component {
  static contextType = CartContext;
  state = {
    products: [],
    loading: true,
    error: '',
  };

  componentDidMount() {
    this.fetchProducts();
  }

  fetchProducts = async () => {
    this.setState({ loading: true });
    try {
      const response = await fetch('https://fakestoreapi.com/products');
      const data = await response.json();
      this.setState({ products: data, loading: false });
    } catch (err) {
      this.setState({ error: 'Failed to load products.', loading: false });
    }
  };

  handleAddToCart = (product) => {
    this.context.addToCart(product);
  };

  render() {
    const { products, loading, error } = this.state;
    return (
      <div style={styles.container}>
        <header style={{ textAlign: 'center', padding: '24px 0', background: '#007bff', color: '#fff', marginBottom: 32, borderRadius: 8, display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 16 }}>
          <svg width="36" height="36" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ verticalAlign: 'middle' }}>
            <path d="M7 18c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm10 0c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm-12.83-2l1.54-7h13.58l1.54 7H4.17zM6.16 7l1.25-5h9.18l1.25 5H6.16z" fill="#fff"/>
          </svg>
          <div>
            <h1 style={{ margin: 0, fontSize: 32, letterSpacing: 2 }}>Gogo Store</h1>
            <p style={{ margin: 0, fontSize: 18, fontWeight: '300' }}>Shop the best products online</p>
          </div>
        </header>
        <h2 style={{ textAlign: 'center', marginBottom: 24, color: '#333' }}>Product Listing</h2>
        {loading ? (
          <div style={{ textAlign: 'center', margin: 32 }}>Loading...</div>
        ) : error ? (
          <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>
        ) : (
          <div style={styles.grid} className='product-grid'>
            {products.map(product => (
              <div key={product.id} style={{ ...styles.card, boxShadow: '0 4px 16px rgba(0,0,0,0.08)', transition: 'transform 0.2s', background: '#f9f9f9' }} className='product-card'>
                <img src={product.image} alt={product.title} style={{ ...styles.img, marginBottom: 16, background: '#fff', padding: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.04)' }} />
                <h3 style={{ margin: '8px 0', fontSize: 20, color: '#222' }}>{product.title}</h3>
                <p style={{ fontWeight: 'bold', color: '#007bff', fontSize: 18, margin: '4px 0' }}>${product.price.toFixed(2)}</p>
                <Link to={`/product/${product.id}/details`} style={{ color: '#007bff', textDecoration: 'underline', marginBottom: 8, fontSize: 15 }}>View Details</Link>
                <button onClick={() => this.handleAddToCart(product)} style={{ ...styles.button, width: '100%', fontSize: 16, marginTop: 12 }}>
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        )}
        <style>{`
          @media (max-width: 600px) {
            .product-grid {
              flex-direction: column !important;
              gap: 12px !important;
            }
            .product-card {
              width: 100% !important;
              max-width: 320px !important;
              margin: 0 auto 16px auto !important;
              box-shadow: 0 2px 8px rgba(0,0,0,0.08) !important;
            }
          }
        `}</style>
      </div>
    );
  }
}

export default HomePage;
