import React, { Component } from 'react';
import { CartContext } from '../context/CartContext';
import { useParams, Link } from 'react-router-dom';

function withRouter(Component) {
  return function Wrapper(props) {
    const params = useParams();
    return <Component {...props} params={params} />;
  };
}

class ProductDetailPage extends Component {
  static contextType = CartContext;
  state = {
    product: null,
    loading: true,
    error: '',
  };

  componentDidMount() {
    this.fetchProduct();
  }

  fetchProduct = async () => {
    const { id } = this.props.params;
    try {
      const response = await fetch(`https://fakestoreapi.com/products/${id}`);
      if (!response.ok) throw new Error('Product not found');
      const data = await response.json();
      this.setState({ product: data, loading: false });
    } catch (err) {
      this.setState({ error: 'Failed to load product.', loading: false });
    }
  };

  handleAddToCart = () => {
    const { product } = this.state;
    if (product) {
      this.context.addToCart(product);
    }
  };

  render() {
    const { product, loading, error } = this.state;
    if (loading) return <div style={{ padding: 16 }}>Loading...</div>;
    if (error || !product) return <div style={{ padding: 16, color: 'red' }}>{error || 'Product not found.'}</div>;
    return (
      <div style={{ padding: 16 }}>
        <Link to='/' style={{ color: '#007bff', textDecoration: 'underline', marginBottom: 16, display: 'inline-block' }}>Back to Home</Link>
        <h2>{product.title}</h2>
        <img src={product.image} alt={product.title} style={{ width: 300, maxWidth: '100%', borderRadius: 8 }} />
        <p>{product.description}</p>
        <p style={{ fontWeight: 'bold' }}>Price: ${product.price.toFixed(2)}</p>
        <button onClick={this.handleAddToCart} style={{ padding: '8px 16px', background: '#007bff', color: '#fff', border: 'none', borderRadius: 4, cursor: 'pointer', fontWeight: 'bold', marginTop: 12 }}>Add to Cart</button>
      </div>
    );
  }
}

export default withRouter(ProductDetailPage);
