import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const styles = {
  container: {
    padding: 16,
  },
  grid: {
    display: 'flex',
    gap: 24,
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  card: {
    border: '1px solid #ccc',
    padding: 16,
    width: 220,
    boxSizing: 'border-box',
    borderRadius: 8,
    background: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    marginBottom: 24,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  img: {
    width: '100%',
    maxWidth: 180,
    borderRadius: 4,
    marginBottom: 8,
  },
  button: {
    marginTop: 8,
    padding: '8px 16px',
    background: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: 4,
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background 0.2s',
  },
};

class HomePage extends Component {
  static contextType = CartContext;
  state = {
    products: [],
    loading: true,
    error: '',
  };

  componentDidMount() {
    this.fetchProducts();
  }

  fetchProducts = async () => {
    this.setState({ loading: true });
    try {
      const response = await fetch('https://fakestoreapi.com/products');
      const data = await response.json();
      this.setState({ products: data, loading: false });
    } catch (err) {
      this.setState({ error: 'Failed to load products.', loading: false });
    }
  };

  handleAddToCart = (product) => {
    this.context.addToCart(product);
  };

  render() {
    const { products, loading, error } = this.state;
    return (
      <div style={styles.container}>
        <header style={{ textAlign: 'center', padding: '40px 0', background: 'linear-gradient(90deg, #007bff 60%, #00c6ff 100%)', color: '#fff', marginBottom: 48, borderRadius: 16, display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 24, boxShadow: '0 8px 32px rgba(0,0,0,0.12)' }}>
          <svg width="44" height="44" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ verticalAlign: 'middle', marginRight: 16 }}>
            <path d="M7 18c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm10 0c-1.104 0-2 .896-2 2s.896 2 2 2 2-.896 2-2-.896-2-2-2zm-12.83-2l1.54-7h13.58l1.54 7H4.17zM6.16 7l1.25-5h9.18l1.25 5H6.16z" fill="#fff"/>
          </svg>
          <div>
            <h1 style={{ margin: 0, fontSize: 40, letterSpacing: 2, fontWeight: 800, textShadow: '0 4px 16px rgba(0,0,0,0.12)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>Gogo Store</h1>
            <p style={{ margin: 0, fontSize: 22, fontWeight: '400', letterSpacing: 1, fontFamily: 'Segoe UI, Arial, sans-serif' }}>Shop the best products online</p>
          </div>
        </header>
        <h2 style={{ textAlign: 'center', marginBottom: 40, color: '#222', fontSize: 32, fontWeight: 700, fontFamily: 'Segoe UI, Arial, sans-serif' }}>Product Listing</h2>

        {/* Image Gallery Section */}
        <div style={{ maxWidth: 1200, margin: '0 auto 32px auto', display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center', alignItems: 'center', background: 'linear-gradient(90deg, #e3f0ff 60%, #f8faff 100%)', borderRadius: 16, padding: 24, boxShadow: '0 4px 16px rgba(0,0,0,0.06)' }}>
          {products.map(product => (
            <img key={product.id} src={product.image} alt={product.title} style={{ width: 120, height: 120, objectFit: 'contain', borderRadius: 12, background: '#fff', boxShadow: '0 2px 8px rgba(0,0,0,0.04)', margin: 8 }} />
          ))}
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', margin: 32 }}>Loading...</div>
        ) : error ? (
          <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>
        ) : (
          <div style={{ ...styles.grid, maxWidth: 1200, margin: '0 auto' }} className='product-grid'>
            {products.map(product => (
              <div key={product.id} style={{ ...styles.card, boxShadow: '0 12px 36px rgba(0,123,255,0.10)', transition: 'transform 0.2s, box-shadow 0.2s', background: 'linear-gradient(135deg, #f8faff 80%, #e3f0ff 100%)', border: 'none', cursor: 'pointer', position: 'relative', overflow: 'hidden', borderRadius: 20, padding: 24 }} className='product-card'
                onMouseEnter={e => {e.currentTarget.style.transform = 'scale(1.05)'; e.currentTarget.style.boxShadow = '0 16px 48px rgba(0,123,255,0.18)';}}
                onMouseLeave={e => {e.currentTarget.style.transform = 'scale(1)'; e.currentTarget.style.boxShadow = '0 12px 36px rgba(0,123,255,0.10)';}}>
                <img src={product.image} alt={product.title} style={{ ...styles.img, marginBottom: 20, background: '#fff', padding: 16, borderRadius: 14, boxShadow: '0 4px 16px rgba(0,0,0,0.08)', transition: 'box-shadow 0.2s', width: '80%', maxWidth: 200 }} />
                <h3 style={{ margin: '12px 0', fontSize: 24, color: '#007bff', fontWeight: 700, fontFamily: 'Segoe UI, Arial, sans-serif' }}>{product.title}</h3>
                <p style={{ fontWeight: 'bold', color: '#222', fontSize: 21, margin: '8px 0', fontFamily: 'Segoe UI, Arial, sans-serif' }}>${product.price.toFixed(2)}</p>
                <Link to={`/product/${product.id}/details`} style={{ color: '#00c6ff', textDecoration: 'underline', marginBottom: 12, fontSize: 17, fontWeight: 500, outline: 'none', fontFamily: 'Segoe UI, Arial, sans-serif' }} tabIndex={0} aria-label={`View details for ${product.title}`}>View Details</Link>
                <button onClick={() => this.handleAddToCart(product)} style={{ ...styles.button, width: '100%', fontSize: 18, marginTop: 20, background: 'linear-gradient(90deg, #007bff 60%, #00c6ff 100%)', boxShadow: '0 4px 16px rgba(0,0,0,0.10)', outline: 'none', border: 'none', fontWeight: 700, letterSpacing: 1, transition: 'background 0.2s, box-shadow 0.2s', fontFamily: 'Segoe UI, Arial, sans-serif' }} aria-label={`Add ${product.title} to cart`}>
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        )}
        <style>{`
          @media (max-width: 600px) {
            .product-grid {
              flex-direction: column !important;
              gap: 20px !important;
              max-width: 100% !important;
            }
            .product-card {
              width: 100% !important;
              max-width: 360px !important;
              margin: 0 auto 22px auto !important;
              box-shadow: 0 8px 32px rgba(0,123,255,0.12) !important;
              border-radius: 20px !important;
              padding: 20px !important;
            }
          }
        `}</style>
      </div>
    );
  }
}

export default HomePage;
