import React, { useState, useEffect } from "react";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  // ✅ API se products fetch
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setProducts(data));
  }, []);

  // ✅ Add to Cart with quantity
  const addToCart = (product) => {
    const exist = cart.find((item) => item.id === product.id);
    if (exist) {
      setCart(
        cart.map((item) =>
          item.id === product.id ? { ...item, qty: item.qty + 1 } : item
        )
      );
    } else {
      setCart([...cart, { ...product, qty: 1 }]);
    }
  };

  // ✅ Decrease Quantity
  const decreaseQty = (id) => {
    const exist = cart.find((item) => item.id === id);
    if (exist.qty === 1) {
      setCart(cart.filter((item) => item.id !== id));
    } else {
      setCart(
        cart.map((item) =>
          item.id === id ? { ...item, qty: item.qty - 1 } : item
        )
      );
    }
  };

  // ✅ Remove Item
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  return (
    <div style={styles.app}>
      {/* 🔹 Header */}
      <header style={styles.header}>
        <h1>🛒 My Store</h1>
        <button style={styles.cartBtn} onClick={() => setShowCart(!showCart)}>
          Cart ({cart.reduce((total, item) => total + item.qty, 0)})
        </button>
      </header>

      {/* 🔹 Product Grid */}
      <div style={styles.grid}>
        {products.map((product) => (
          <div key={product.id} style={styles.card}>
            <div style={styles.imageWrap}>
              <img
                src={product.image}
                alt={product.title}
                style={styles.cardImg}
              />
            </div>
            <h3>{product.title.slice(0, 30)}...</h3>
            <p>₹{product.price}</p>
            <button style={styles.addBtn} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* 🔹 Cart Drawer */}
      {showCart && (
        <div style={styles.cartDrawer}>
          <h2>Your Cart</h2>
          {cart.length === 0 ? (
            <p>No items added</p>
          ) : (
            cart.map((item) => (
              <div key={item.id} style={styles.cartItem}>
                <span>{item.title.slice(0, 20)}...</span>
                <div style={styles.qtyBox}>
                  <button onClick={() => decreaseQty(item.id)}>-</button>
                  <span>{item.qty}</span>
                  <button onClick={() => addToCart(item)}>+</button>
                </div>
                <button onClick={() => removeFromCart(item.id)}>❌</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

const styles = {
  app: { fontFamily: "sans-serif", padding: 20 },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  cartBtn: {
    padding: "8px 16px",
    background: "#2563eb",
    color: "#fff",
    border: "none",
    borderRadius: 8,
    cursor: "pointer",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,minmax(220px,1fr))",
    gap: 16,
    marginTop: 20,
  },
  card: {
    border: "1px solid #ddd",
    borderRadius: 12,
    padding: 16,
    textAlign: "center",
    background: "#fff",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
  },
  imageWrap: {
    height: 200,
    width: "100%",
    display: "grid",
    placeItems: "center",
    background: "#f9fafb",
    borderRadius: 12,
  },
  cardImg: {
    width: 120,
    height: 120,
    objectFit: "contain",
  },
  addBtn: {
    marginTop: 10,
    padding: "6px 12px",
    background: "#16a34a",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
  },
  // ✅ Responsive Cart Drawer
  cartDrawer: {
    position: "fixed",
    right: 0,
    top: 0,
    height: "100%",
    width: "100%", // default for mobile
    maxWidth: "400px", // desktop limit
    background: "#fff",
    boxShadow: "-2px 0 6px rgba(0,0,0,0.1)",
    padding: 20,
    overflowY: "auto",
    transition: "0.3s ease",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "8px 0",
    borderBottom: "1px solid #eee",
  },
  qtyBox: {
    display: "flex",
    alignItems: "center",
    gap: 8,
  },
};

export default App;
